import { WDIO_EVENT_NAME } from '../../constants.js';
/**
 * a Vite plugin to help communicate with the worker process
 */
export function workerPlugin(onSocketEvent) {
    return {
        name: 'wdio:worker',
        configureServer({ ws }) {
            ws.on(WDIO_EVENT_NAME, onSocketEvent);
        }
    };
}
